    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed
        try {
            String tipoEquipo = rbtLaptop.isSelected() ? "Laptop" : "Desktop";
            String modelo = txtCpu.getText();
            int discoDuro = Integer.parseInt(txtDiscoDuro.getText());
            int ramEquipo = Integer.parseInt(txtRam.getText());
            double precioEquipo = Double.parseDouble(txtPrecio.getText());
            String pregunta1 = txtPregunta1.getText();
            String pregunta2 = txtPregunta2.getText();
            String descripcionEquipo = txtDescripcion.getText();

            validarCampoNoVacio(modelo, "CPU");
            validarNumeroPositivo(discoDuro, "Disco Duro");
            validarNumeroPositivo(ramEquipo, "RAM");
            validarNumeroPositivo(precioEquipo, "Precio");

            if (tipoEquipo.equals("Laptop")) {
                validarNumeroPositivo(Integer.parseInt(pregunta1), "Tamaño pantalla (in)");
                validarNumeroPositivo(Integer.parseInt(pregunta2), "Cantidad de puertos USB");
            } else {
                validarCampoNoVacio(pregunta1, "Fuente de poder");
                validarCampoNoVacio(pregunta2, "Factor de forma");
            }

            ControladorEquipo.registrarEquipo(tipoEquipo, modelo, discoDuro, ramEquipo, precioEquipo, pregunta1, pregunta2, descripcionEquipo);

            limpiarCampos();
            mostrarMensaje("Equipo registrado exitosamente.", "Registro Exitoso");
        } catch (NumberFormatException e) {
            mostrarError("Por favor, ingrese valores numéricos válidos.");
        } catch (Exception e) {
            mostrarError("Error al registrar el equipo. Por favor, inténtelo de nuevo.");
        }
    }//GEN-LAST:event_btnGuardarActionPerformed

