    private void limpiarCampos() {
    txtCpu.setText("");
    txtDiscoDuro.setText("");
    txtRam.setText("");
    txtPrecio.setText("");
    txtPregunta1.setText("");
    txtPregunta2.setText("");
    txtDescripcion.setText("");
    pregunta1.setText("...");
    pregunta2.setText("...");
    }
    
    private void mostrarError(String mensaje) {
        JOptionPane.showMessageDialog(this, mensaje, "Error de Validación", JOptionPane.ERROR_MESSAGE);
    }

    private void mostrarMensaje(String mensaje, String titulo) {
        JOptionPane.showMessageDialog(this, mensaje, titulo, JOptionPane.INFORMATION_MESSAGE);
    }

    private void validarNumero(String valor, String campo) {
        try {
            int numero = Integer.parseInt(valor);
            if (numero <= 0) {
                mostrarError("Por favor, ingrese un valor numérico positivo para " + campo);
            }
        } catch (NumberFormatException e) {
            mostrarError("Por favor, ingrese un valor numérico válido para " + campo);
        }
    }

    private void validarFuenteDePoder(String valor) {
        if (valor.isEmpty()) {
            mostrarError("Por favor, ingrese información para la fuente de poder.");
        }
    }

    private void validarFactorDeForma(String valor) {
        if (valor.isEmpty()) {
            mostrarError("Por favor, ingrese información para el factor de forma.");
        }
    }
    
    private void validarCampoNoVacio(String valor, String campo) {
        if (valor.isEmpty()) {
            mostrarError("Por favor, ingrese un valor para " + campo);
        }
    }

    private void validarNumeroPositivo(double numero, String campo) {
        if (numero <= 0) {
            mostrarError("Por favor, ingrese un valor numérico positivo para " + campo);
        }
    }
